declare module '*.scss' {
  const css: { [key: string]: string }
  export default css
}

declare module '*.svg' {
  const value: string
  export = value
}

declare module '*.png' {
  const value: string
  export = value
}

declare module '*.jpg' {
  const value: string
  export = value
}

declare module '*.jpeg' {
  const value: string
  export = value
}

declare module '*.gif' {
  const value: string
  export = value
}

declare module '*.ico' {
  const value: string
  export = value
}
